<script type="text/javascript"><!--
var geocoder = new google.maps.Geocoder();
var infowindow = new google.maps.InfoWindow();
var map = null;
var START_RED = 4;
var delay = 100;
var allMarkers = [];
var markers = '';
function stripslashes(str) {
    str = str.replace(/\\'/g, '\'');
    str = str.replace(/\\"/g, '"');
    str = str.replace(/\\0/g, '\0');
    str = str.replace(/\\\\/g, '\\');
    return str;
}

// to show initial map when popup is loaded.
jQuery( window ).load(function() {
 // get markers data, strip slashes and convert to array using json.parse
 markers = JSON.parse(stripslashes(jQuery('#hiddendealerslist').val()));
  initializeMap();
})

// to show initial map
initializeMap = function() {
    if (geocoder == null) {
    	geocoder = new google.maps.Geocoder();
	}
	var latlon='23.00, 72.00';
	var mapOptions = {
	    center: new google.maps.LatLng(latlon),
	    zoom: START_RED,
	    mapTypeId: google.maps.MapTypeId.ROADMAP,
	    mapTypeControl: true,
    	scrollwheel: false,
	    scaleControl: true
	};
	map = new google.maps.Map(document.getElementById("map_canvas"),mapOptions);	
	//theNext();

	var bounds = new google.maps.LatLngBounds();
    var marker, i;
    for (i = 0; i < markers.length; i++) {
        marker = new google.maps.Marker({
            position: new google.maps.LatLng(markers[i].latitude, markers[i].longitude),
            map: map,
            title: markers[i].title,
            clickable: true
        });

        (function (marker, i) {
            google.maps.event.addListener(marker, 'click', function () {
                var html = '<h3>' + markers[i].title + '</h3>';
                html += '<p>';
                html += markers[i].address + '<br>';
                html += markers[i].phone + '<br>';
                html += '<a target="_blank" class="dark" href="' + markers[i].website + '">' + markers[i].website + '<a>';
                html += '</p>';
                infowindow = new google.maps.InfoWindow({
                    content: html
                });
                infowindow.open(map, marker);
            });
        })(marker, i);
		bounds.extend(marker.getPosition());
        marker.dataItem = markers[i].id;
        allMarkers.push(marker);
    }
	map.fitBounds(bounds);

    // to change the list of visible salons whenever there is any change in map(like zoom,drag,search etc)
	google.maps.event.addListener(map, 'idle', function () {
	    showLocations();
	});
}

function showLocations() {
    jQuery('.salon-item').fadeOut();
    var visibleItem = 0;
    for (var x = 0; x < allMarkers.length; x++) {
        if (map.getBounds().contains(allMarkers[x].getPosition())) {
            jQuery('.salon-item[data-id="' + allMarkers[x].dataItem + '"]').fadeIn();
            visibleItem++;
        }
    }
    if (visibleItem > 0) {
        jQuery('.location-title').animate({opacity: 1});
    } else {
        jQuery('.location-title').animate({opacity: 0});
    }
}

jQuery(document).ready(function () {
	jQuery('#searchlocation').on('click', function (e) { 
	    e.preventDefault();
	    var zipcode = jQuery('#zipcode').val();
	    if(zipcode=='Postal Code / Zip Code'){
	    	zipcode='';
	    }
	    valid = false;

	    if( (zipcode != '' )){
	        valid = true;
	    }

	    if (valid) {
	        var address = zipcode;
	        geocoder.geocode({'address': address}, function (results, status) {
	            if (status == google.maps.GeocoderStatus.OK) {
	                map.fitBounds(results[0].geometry.bounds);
	                if(address != zipcode){
        				map.setZoom(6);
	                }
	            }
	        });
	    	document.getElementById('map_canvas').scrollIntoView();
	    }
	});
});

</script>

